
#include <stdio.h>
#include <stdlib.h>

char input[] = "";

int main() {
  int inputLength = 650;
  int totalLength = 650*10000;

  int offset = 0;
  for(int i=6, p=1; i >= 0; i--, p *= 10) {
    offset += (input[i]-0x30)*p;
  }

  int z = totalLength - offset;

  char* arr = malloc(z);
  for(int k = 0; k < z; k++) {
    arr[k] = input[649- (k%inputLength)] - 0x30;
  }

  for(int n=0; n < 100; n++) {

    //printf("Phase nÂ°%d\n", n+1);

    for(int k = 0, sum = 0; k < z; k++) {
      sum = (sum + arr[k]) % 10;
      arr[k] = sum;
    }

  }

  printf("Message: ");
  for(int k=z-1; k >= z-8; k--) {
    printf("%d", arr[k]);
  }
  printf("\n");

}
